mapper.update:

update words
set dictionary_id = #{dictionaryId,jdbcType=BIGINT},
  spelling = #{spelling,jdbcType=VARCHAR},
  pronunciation = #{pronunciation,jdbcType=VARCHAR},
  explanation = #{explanation,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:

select id, dictionary_id, spelling, pronunciation, explanation
from words
where id = #{id,jdbcType=BIGINT}


select id, dictionary_id, spelling, pronunciation, explanation
from words
where dictionary_id = #{dictionaryId,jdbcType=BIGINT}
